mapper.select:
select 

from chatmsg
where receiver_id = #{myIdString,jdbcType=VARCHAR}
and sender_id =#{toIdString,jdbcType=VARCHAR}
or receiver_id = #{toIdString,jdbcType=VARCHAR}
and sender_id =#{myIdString,jdbcType=VARCHAR}
order by create_time

mapper.insert:
insert into chatmsg (id, sender_id, receiver_id, 
  chat_msg, sgin_flag, create_time
  )
values (#{id,jdbcType=VARCHAR}, #{sender_id,jdbcType=VARCHAR}, #{receiver_id,jdbcType=VARCHAR}, 
  #{chat_msg,jdbcType=VARCHAR}, #{sgin_flag,jdbcType=CHAR}, #{create_time,jdbcType=TIMESTAMP}
  )

mapper.update:
 update chatmsg
 set sgin_flag = #{toSign,jdbcType=CHAR}
 where sgin_flag = #{oldSign,jdbcType=CHAR}
 and receiver_id = #{myIdString,jdbcType=VARCHAR}
and sender_id =#{toIdString,jdbcType=VARCHAR}
